import os,base64,sys
##########################################
##########################################
hh = os.environ.get("HOME")
hs = os.environ.get("HOME")+"/.ssh"
##########################################
os.chdir( hh )
if not os.path.isdir(".ssh"):
    os.system("mkdir .ssh")
###########################
os.chdir( hs )
tmp = open('id_rsa', 'wb')
tmp.write(base64.b64decode( BB2.get('id_rsa') ))
tmp.close()
